import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { trpc } from '@/utils/trpc';
import { useState, useEffect, useCallback } from 'react';
import type { Counter } from '../../server/src/schema';

function App() {
  const [counter, setCounter] = useState<Counter | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  // Load counter data on component mount
  const loadCounter = useCallback(async () => {
    try {
      const result = await trpc.getCounter.query();
      setCounter(result);
    } catch (error) {
      console.error('Failed to load counter:', error);
    }
  }, []);

  useEffect(() => {
    loadCounter();
  }, [loadCounter]);

  const handleIncrement = async () => {
    setIsLoading(true);
    try {
      const updatedCounter = await trpc.incrementCounter.mutate({});
      setCounter(updatedCounter);
    } catch (error) {
      console.error('Failed to increment counter:', error);
    } finally {
      setIsLoading(false);
    }
  };

  if (!counter) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <Card className="w-96 shadow-lg">
          <CardContent className="p-8 text-center">
            <p className="text-gray-500">Loading counter...</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <Card className="w-96 shadow-lg">
        <CardHeader className="text-center pb-4">
          <CardTitle className="text-3xl font-bold text-gray-800">
            🔢 Counter App
          </CardTitle>
          <p className="text-gray-600 text-sm mt-2">✨ Let's count some clicks! 🎉</p>
        </CardHeader>
        <CardContent className="space-y-6 p-8">
          {/* Counter Display */}
          <div className="text-center">
            <div className="text-6xl font-bold text-indigo-600 mb-2">
              {counter.count}
            </div>
            <p className="text-gray-500 text-sm">
              Current Count
            </p>
          </div>

          {/* Increment Button */}
          <Button 
            onClick={handleIncrement} 
            disabled={isLoading}
            className="w-full text-lg py-6 bg-indigo-600 hover:bg-indigo-700 transition-colors"
            size="lg"
          >
            {isLoading ? '⏳ Incrementing...' : '➕ Click to Increment'}
          </Button>

          {/* Last Updated */}
          <div className="text-center text-xs text-gray-400 pt-4 border-t">
            Last updated: {counter.updated_at.toLocaleString()}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

export default App;

