import { db } from '../db';
import { countersTable } from '../db/schema';
import { type IncrementCounterInput, type Counter } from '../schema';
import { eq, sql } from 'drizzle-orm';

export const incrementCounter = async (input: IncrementCounterInput): Promise<Counter> => {
  try {
    const counterId = input.id || 1; // Default to counter ID 1 if not provided

    // First, ensure the counter exists
    let counter = await db.select()
      .from(countersTable)
      .where(eq(countersTable.id, counterId))
      .execute();

    if (counter.length === 0) {
      // Create the counter if it doesn't exist
      const newCounter = await db.insert(countersTable)
        .values({
          id: counterId,
          count: 1 // Start at 1 since this is an increment operation
        })
        .returning()
        .execute();

      return newCounter[0];
    }

    // Increment the existing counter
    const result = await db.update(countersTable)
      .set({
        count: sql`${countersTable.count} + 1`,
        updated_at: sql`now()`
      })
      .where(eq(countersTable.id, counterId))
      .returning()
      .execute();

    return result[0];
  } catch (error) {
    console.error('Counter increment failed:', error);
    throw error;
  }
};
