import { afterEach, beforeEach, describe, expect, it } from 'bun:test';
import { resetDB, createDB } from '../helpers';
import { db } from '../db';
import { countersTable } from '../db/schema';
import { type IncrementCounterInput } from '../schema';
import { incrementCounter } from '../handlers/increment_counter';
import { eq } from 'drizzle-orm';

describe('incrementCounter', () => {
  beforeEach(createDB);
  afterEach(resetDB);

  it('should create and increment counter when it does not exist', async () => {
    const input: IncrementCounterInput = { id: 1 };

    const result = await incrementCounter(input);

    expect(result.id).toEqual(1);
    expect(result.count).toEqual(1);
    expect(result.updated_at).toBeInstanceOf(Date);
  });

  it('should increment existing counter', async () => {
    // Create initial counter
    await db.insert(countersTable)
      .values({ id: 1, count: 5 })
      .execute();

    const input: IncrementCounterInput = { id: 1 };
    const result = await incrementCounter(input);

    expect(result.id).toEqual(1);
    expect(result.count).toEqual(6);
    expect(result.updated_at).toBeInstanceOf(Date);
  });

  it('should use default counter ID when not provided', async () => {
    const input: IncrementCounterInput = {};

    const result = await incrementCounter(input);

    expect(result.id).toEqual(1);
    expect(result.count).toEqual(1);
    expect(result.updated_at).toBeInstanceOf(Date);
  });

  it('should save counter changes to database', async () => {
    const input: IncrementCounterInput = { id: 2 };

    const result = await incrementCounter(input);

    // Verify in database
    const counters = await db.select()
      .from(countersTable)
      .where(eq(countersTable.id, 2))
      .execute();

    expect(counters).toHaveLength(1);
    expect(counters[0].id).toEqual(2);
    expect(counters[0].count).toEqual(1);
    expect(counters[0].updated_at).toBeInstanceOf(Date);
  });

  it('should handle multiple sequential increments', async () => {
    const input: IncrementCounterInput = { id: 3 };

    // First increment - creates counter
    const result1 = await incrementCounter(input);
    expect(result1.count).toEqual(1);

    // Second increment
    const result2 = await incrementCounter(input);
    expect(result2.count).toEqual(2);

    // Third increment
    const result3 = await incrementCounter(input);
    expect(result3.count).toEqual(3);

    // Verify final state in database
    const counters = await db.select()
      .from(countersTable)
      .where(eq(countersTable.id, 3))
      .execute();

    expect(counters[0].count).toEqual(3);
  });
});
