export const getCounter = async (): Promise<Counter> => {
  try {
    // Get the first counter, or create one if none exists
    const result = await db.select()
      .from(countersTable)
      .limit(1)
      .execute();

    if (result.length === 0) {
      // Create default counter if none exists
      const newCounter = await db.insert(countersTable)
        .values({
          count: 0
        })
        .returning()
        .execute();

      return newCounter[0];
    }

    return result[0];
  } catch (error) {
    console.error('Get counter failed:', error);
    throw error;
  }
};
