import { db } from '../db';
import { countersTable } from '../db/schema';
import { type IncrementCounterInput, type Counter } from '../schema';
import { sql } from 'drizzle-orm';

export const incrementCounter = async (input: IncrementCounterInput): Promise<Counter> => {
  try {
    // Use PostgreSQL's INSERT ... ON CONFLICT to handle both insert and update
    // This ensures we always have exactly one counter record
    const result = await db.execute(sql`
      INSERT INTO counters (id, count) 
      VALUES (1, ${input.increment})
      ON CONFLICT (id) 
      DO UPDATE SET 
        count = counters.count + ${input.increment},
        updated_at = NOW()
      RETURNING *
    `);

    // Extract the first row from the raw result
    const counter = result.rows[0] as any;

    return {
      id: counter.id,
      count: counter.count,
      updated_at: new Date(counter.updated_at)
    };
  } catch (error) {
    console.error('Counter increment failed:', error);
    throw error;
  }
};
