import { afterEach, beforeEach, describe, expect, it } from 'bun:test';
import { resetDB, createDB } from '../helpers';
import { db } from '../db';
import { countersTable } from '../db/schema';
import { type IncrementCounterInput } from '../schema';
import { incrementCounter } from '../handlers/increment_counter';
import { eq } from 'drizzle-orm';

describe('incrementCounter', () => {
  beforeEach(createDB);
  afterEach(resetDB);

  it('should create a new counter with initial increment', async () => {
    const input: IncrementCounterInput = {
      increment: 5
    };

    const result = await incrementCounter(input);

    expect(result.id).toBe(1);
    expect(result.count).toBe(5);
    expect(result.updated_at).toBeInstanceOf(Date);
  });

  it('should increment existing counter', async () => {
    // Create initial counter
    await incrementCounter({ increment: 3 });

    // Increment it again
    const result = await incrementCounter({ increment: 7 });

    expect(result.id).toBe(1);
    expect(result.count).toBe(10); // 3 + 7
    expect(result.updated_at).toBeInstanceOf(Date);
  });

  it('should use default increment of 1', async () => {
    // Test with input that will use the default increment value
    const input: IncrementCounterInput = {
      increment: 1
    };

    const result = await incrementCounter(input);

    expect(result.count).toBe(1);
  });

  it('should save counter to database', async () => {
    const input: IncrementCounterInput = {
      increment: 15
    };

    const result = await incrementCounter(input);

    // Verify it's actually saved in the database
    const counters = await db.select()
      .from(countersTable)
      .where(eq(countersTable.id, result.id))
      .execute();

    expect(counters).toHaveLength(1);
    expect(counters[0].count).toBe(15);
    expect(counters[0].updated_at).toBeInstanceOf(Date);
  });

  it('should handle multiple increments correctly', async () => {
    // First increment
    await incrementCounter({ increment: 2 });

    // Second increment
    await incrementCounter({ increment: 3 });

    // Third increment
    const result = await incrementCounter({ increment: 5 });

    expect(result.count).toBe(10); // 2 + 3 + 5

    // Verify only one counter record exists
    const allCounters = await db.select()
      .from(countersTable)
      .execute();

    expect(allCounters).toHaveLength(1);
    expect(allCounters[0].count).toBe(10);
  });
});
