import { db } from '../db';
import { countersTable } from '../db/schema';
import { type IncrementCounterInput, type Counter } from '../schema';
import { eq } from 'drizzle-orm';

export const incrementCounter = async (input: IncrementCounterInput): Promise<Counter> => {
  try {
    // Check if a counter exists (assuming we only have one counter with id=1)
    const existingCounter = await db.select()
      .from(countersTable)
      .where(eq(countersTable.id, 1))
      .execute();

    if (existingCounter.length === 0) {
      // Create new counter with the increment value
      const result = await db.insert(countersTable)
        .values({
          count: input.increment
        })
        .returning()
        .execute();

      return result[0];
    } else {
      // Update existing counter by incrementing
      const result = await db.update(countersTable)
        .set({
          count: existingCounter[0].count + input.increment,
          updated_at: new Date()
        })
        .where(eq(countersTable.id, 1))
        .returning()
        .execute();

      return result[0];
    }
  } catch (error) {
    console.error('Counter increment failed:', error);
    throw error;
  }
};
