    return Promise.resolve({
        id: 0, // Placeholder ID
        name: input.name,
        description: input.description || null, // Handle nullable field
        price: input.price,
        stock_quantity: input.stock_quantity,
        created_at: new Date() // Placeholder date
    } as Product
    )
}

</file>

