import { db } from '../db';
import { countersTable } from '../db/schema';
import { eq } from 'drizzle-orm';
import { type CounterWithMessage } from '../schema';

export async function getCounter(id?: number): Promise<CounterWithMessage> {
    const counterId = id || 1; // Default to counter ID 1

    try {
        // Fetch counter from database
        const results = await db.select()
            .from(countersTable)
            .where(eq(countersTable.id, counterId))
            .execute();

        // If counter doesn't exist, create it with default values
        if (results.length === 0) {
            const newCounterResults = await db.insert(countersTable)
                .values({
                    count: 0
                })
                .returning()
                .execute();

            const newCounter = newCounterResults[0];
            const message = generateFunMessage(newCounter.count);

            return {
                id: newCounter.id,
                count: newCounter.count,
                message: message,
                created_at: newCounter.created_at,
                updated_at: newCounter.updated_at
            };
        }

        const counter = results[0];
        const message = generateFunMessage(counter.count);

        return {
            id: counter.id,
            count: counter.count,
            message: message,
            created_at: counter.created_at,
            updated_at: counter.updated_at
        };
    } catch (error) {
        console.error('Failed to get counter:', error);
        throw error;
    }
}

function generateFunMessage(count: number): string {
    // Generate different messages based on count milestones
    if (count === 0) {
        return "🎯 Ready to start counting? Let's go! 🚀";
    } else if (count === 1) {
        return "🎉 First click! You're on your way! ✨";
    } else if (count < 5) {
        return `🌟 You've clicked ${count} times! Keep going! 💪`;
    } else if (count < 10) {
        return `🔥 ${count} clicks and counting! You're on fire! 🚀`;
    } else if (count < 25) {
        return `🎊 Wow! ${count} clicks! You're unstoppable! 💫`;
    } else if (count < 50) {
        return `🏆 Amazing! ${count} clicks! You're a clicking champion! 🎯`;
    } else if (count < 100) {
        return `🚀 Incredible! ${count} clicks! You're reaching for the stars! ⭐`;
    } else if (count < 500) {
        return `🎉 Phenomenal! ${count} clicks! You're a clicking legend! 👑`;
    } else if (count < 1000) {
        return `💎 Outstanding! ${count} clicks! You're absolutely brilliant! ✨`;
    } else {
        return `🌟 LEGENDARY! ${count} clicks! You've achieved greatness! 🏆👑🎊`;
    }
}
